import React, { useState } from 'react'
import { Link } from "react-router-dom";
import CCustomer from './CCustomer'
import DCustomer from './DCustomer'
import RCustomer from './RCustomer'
import UCustomer from './RCustomer'

import { Navbar, Button, Table } from 'react-bootstrap';



const CustomerIndex =()=>{

    const currentDomain = '/Customer';
    const [booleans, setBooleans] = useState({
        C : false,
        R : false,
        U: false,
        D: false
    })

    const setDisplay =(C, R, U, D)=>{
        setBooleans({
            C: C,
            R: R,
            U: U,
            D: D
        })
    }

    const clicked = (code)=>{
        setDisplay()
    }

    const names=[
        {
            label: "Add Customer",
            code: 'C'
        },{
            label: "View Registered Customer",
            code: 'R'
        },{
            label: "Update Customer Info",
            code: 'U'
        },{
            label: "Delete Customer",
            code: 'D'
        }
    ]

    const linkInfo = names.map(
    (i)=>{
        return (
                <button className='btn btn-outline-danger' onClick={clicked(i.code)}>
                    {i.label}
                </button>
                )
        }
    )
    const CCustomer = [0].map(
        (i)=>{
            return(
                <div>
                    <h1>This is C Customer</h1>
                </div>
            )
        }
    )

    return(
        <div style={{textAlign:'center'}} >
            <h2 >Manage Customer information</h2>
                {linkInfo}
        </div>
    )
}

export default CustomerIndex;
