import React, { useState } from 'react'
import axios from 'axios'
import Search from './Search/Search'
import { primaryKey } from './db'


// prpos: mainTable, selectTable = [array]

const SelectOptions=(props)=>{
    const [showDisplay, setShowDisplay] = useState(false)
    const [gotData, setGotData] = useState([])
    const [fk, setfk] =useState([])
    const [FK, setFK] = useState('')
    const [showSearchOption, setShowSearchOption] = useState(false)

    const getData=()=>{
        var sql = "SELECT " + primaryKey[props.selectTable] +  " FROM " + props.selectTable + ";"
        axios.post('/api/show_data/', {sql:sql}).then(res=>{
            setGotData(res.data) 
            console.log(res.data)
        }).catch(err=>{
            console.log(err)
        })
        setShowDisplay(true)
        console.log(fk)
    }

    const CusidDD = Object.keys(gotData).map(
            (key)=>{
                return (
                    <>
                        <option>{gotData[key][primaryKey[props.selectTable]]}</option>
                    </>
                )
            }
    )

    const selects = [2, 1].map(
        (key)=>{
            return(
                <>
                    <span class="input-group-text">Select {props.selectTable} ID</span> 
                    <select class="form-control" onChange={(e)=>{if(e.target.value === "--SELECT--"){setFK('')}else{setFK(e.target.value)}}}>
                        <option>--SELECT--</option> {CusidDD}
                    </select> 
                </>
            )
        }
    )


    return(
        <div>
            <hr />
                <button onClick={getData} className='btn btn-danger' >Create A New {props.mainTable}</button>
            <hr />
                {showDisplay &&
                <>
                    <div className="aa">
                        <form onSubmit={(e)=>{e.preventDefault()}}>
                            <div  class="input-group-prepend" >
                                {selects}
                            </div>

                        </form>

                    </div>
                    <div className='inf'>
                        <button className='btn btn-primary' onClick={()=>{setShowSearchOption(true)}}>Search For {props.selectTable} ID</button>
                            <br /> <hr />
                            {showSearchOption && 
                            <>
                                <Search table={props.selectTable} ></Search>
                            </>

                            }
                    </div>
                </>
                }
        </div>
    )
}

export default SelectOptions;
