import React, { useState } from 'react'
import { BrowserRouter, Route, Link } from "react-router-dom";
import CCustomer from './CCustomer';
import RCustomer from './RCustomer';
import UCustomer from './UCustomer';
import DCustomer from './DCustomer';


const CustomerIndex =()=>{

    const currentURL = window.location.href;
    const [routing, setRouting] = useState([    // [path, component]
        [currentURL + '/Create', CCustomer],
        [currentURL + '/Read', RCustomer],
        [currentURL + '/Update', UCustomer],
        [currentURL + '/Delete', DCustomer]

    ])

      const routeInfo = routing.map(
        (i)=>{
          return <Route path={i[0]} component={i[1]} exact></Route>
        }
      )
    return(
        <div>
            <BrowserRouter>
                {routeInfo}
            </BrowserRouter>
            <h1>This is Customer</h1>
            <Link to={currentURL + '/Create'}>
                Create
            </Link>
        </div>
    )
}

export default CustomerIndex;
