import React, { useState } from 'react'
import { SearchKeys, columns, dataType, keyValue } from './db'
import axios from 'axios'
import Display from './Display'
import Decorate from './Decorate'

/*
props needed:
    table
*/


const ComplexSearch =(props)=> {

    const [showDisplay, setShowDisplay] = useState(false)
    const [gotData, setgotData] = useState([])
    const [temp, setTemp] = useState('')
    const [showSearchSegment, setShowSearchSegment] = useState(false)
    const [info, setinfo] = useState({
        table: props.table,
        allSearchParas: Object.keys(SearchKeys[props.table]),
        maxSearchParas: Object.keys(SearchKeys[props.table]).length,
        numberOfSearchParas: 1,
        searchParas: Object.keys(SearchKeys[props.table]),
        keyValue: keyValue[props.table]("")
    })

    const options = info.searchParas.map(
                (i)=>{
                    return(
                        <option>{i}</option>
                    )
                })

    const searchSegement = Object.keys(info.keyValue).map(
        (i)=>{
            return (
                <select className='btn btn-outline-danger'>{options}</select>
            )
        }
    )

    const numberOfParameters =(e)=>{
        e.preventDefault()
        console.log(info)
        setShowSearchSegment(true)
    }

    const searchReq =(e)=>{
        e.preventDefault()
    }

    return(
        <div>
        <hr />
        <h3>Advanced Search, with multiple parameters</h3>
        <hr />
        {/* <form onSubmit={numberOfParameters}>
             <label>Enter the number of parameters you want to search with [max:{info.maxSearchParas}]</label>
            <input type='number' min="1" max={info.maxSearchParas} onChange={(e)=>{
                                                        setShowSearchSegment(false)
                                                        if(e.target.value !== ''){
                                                           setinfo({
                                                            ...info,
                                                            numberOfSearchParas: parseInt(e.target.value)
                                                        }) 
                                                        }

                                                        }}/>
        </form> */}
        <form onSubmit={searchReq} style={{paddingLeft:'5%'}} >
            <div className="navbar navbar-expand">
                {
                    (
                        ()=>{
                            if(showSearchSegment){
                                return (
                                    <div>
                                        {searchSegement}
                                        <input className='btn btn-outline-danger' type="submit" value='Done'/>
                                    </div>
                                )
                            }
                        }
                    )()
                }

            </div>

        </form>
        <hr />
            {
                (
                    ()=>{
                        if(showDisplay){
                            return(
                                <div>
                                    <hr />
                                    <Decorate style={{textAlign:'right'}} displayText={'>  SEARCH RESULT FROM PROVIDED REQUEST  <'} ></Decorate>
                                    <hr />
                                    <Display gotData={gotData} db2n={columns[info.table]}></Display>
                                    <div>
                                    </div>
                                </div>
                            )
                        }
                    }
                )()
            }
        </div>
    )
}

export default ComplexSearch;
