import React, { useState } from 'react'

const CCustomer =()=>{

    const [custInformation, setCustInformation] = useState({
        "First Name": null,
        "Middle Name": null,
        "Last Name": null,
        "Sex": null,
        "Phone Number": null,
        "Email Address": null,
        "Date of birth": null,
        "Address": null,
    })

    const fields = Object.keys(custInformation).map(
        (key)=>{
            return(
                <div>
                    <label>
                        {key}
                        <input type="text" placeholder='Name' onChange={(e)=>{setCustInformation({...custInformation, key:e.target.value})}} />
                    </label>
                    <input type="submit" value="Submit" />
                </div>
                )

        }
    )

    const handleSubmit=(e)=>{
        e.preventDefault()
        console.log(custInformation);
    }

    return(
        <div>
            <h1>This is Create Customer</h1>
            <form onSubmit={handleSubmit}>
                {fields}
            </form>
        </div>
    )
}

export default CCustomer;
