import React, { useState } from 'react'
import '../../css/Modal.css'
import axios from 'axios'

import Display from '../Display'


const Modal = (props)=>{

    const [gotData, setgotData] = useState([])
    const [showDisplay, setShowDisplay] = useState(false)

    const showMayBeItems=()=>{
            if (!props.complexDel){
            var sql = "SELECT * FROM " + props.table + " WHERE " + props.primaryKeyName + " = '" + props.delID + "'"

            axios.post('/api/show_data/', {sql:sql}).then(res=>{
                    setgotData(res.data)
                }).catch(err=>{
                    console.log(err)
                })
            setShowDisplay(true)
        }
    }

    const details =()=>{
        showMayBeItems()
    }


    return( 
        <div className='modalBackground'>
            <div className="modalContainer">
                <div className='title'>
                    <h1>Are you sure to delete?</h1>
                    <hr />
                </div>
                <div className="bodyz">
                    <button onClick={details}>VIEW DETAILS OF DATA</button>
                    <h3>Detail of the data: </h3>
                        {showDisplay && <Display gotData={gotData} ></Display>}
                    </div>
            {!props.complexDel && <div className='footer'>
                    <button onClick={()=>{props.closeModal(false)}}>Cancel</button>
                    <button onClick={()=>props.delWithID()} >Continue</button>
                </div>}
            {props.complexDel && <div className='footer'>
                <button onClick={()=>{props.closeModal(false)}}>Cancel</button>
                <button onClick={props.complexDelz} >Continue</button>
            </div>}
            </div>

        </div>
    )
}

export default Modal;
