import React, { useState } from 'react'
import Search from '../Search';
import Modal from '../Modal/Modal';import axios from 'axios'
import swal from 'sweetalert';

// props: table, primaryKeyName

const Delete=(props)=>{

    const [showDisplay, setShowDisplay] = useState(false)
    const [gotData, setGotData] = useState([])
    const [srarchForCusID, setSearchForCusID] = useState(false)
    const [openModal, setOpenModel] = useState(false);
    const [style1, setStyle1] = useState({position:'sticky', top:'10%'})
    const [delID, setDelID] = useState('')


    const deleteClicked =()=>{
        setStyle1({})
        setOpenModel(true)
    }

    const backEND=(sql)=>{
        axios.post('/api/execute/', {sql:sql}).then(res=>{
            setGotData(res.data)
        }).catch(err=>{
            console.log(err)
        })
        if(gotData.length === 0 || gotData.affectedRows === 0){
            swal("Bad Request", "No data found with such request", "error");
        }
        else{
            swal("DONE", "Your request was successful", "success");
        }
    }

    const delWithID =()=>{
        var sql = "DELETE FROM " + props.table + " WHERE " + props.primaryKeyName + " = '" + delID + "'"
        backEND(sql)
        closeModal()
    }

    const closeModal =()=>{
        setOpenModel(false)
        setStyle1({position:'sticky', top:'10%'})
        setDelID('')
    }

    return(
        <div>
            <hr />
            {/* <div>
                <Delete></Delete>
            </div> */}
            {openModal && <Modal closeModal={closeModal} delID={delID} delWithID={delWithID}></Modal>}
                <div className='inf1' style={style1}>
                    <label className="form-check-label">Enter {props.table} ID to delete</label>
                    <input className="form-control" type="number" value={delID} onChange={(e)=>{setDelID(e.target.value)}}/>
                    <button className='btn btn-outline-danger' onClick={deleteClicked}>DELETE</button>
                    <hr />
                    <button className='btn btn-primary' onClick={()=>{setSearchForCusID(true);}} >Search for Customer ID</button>
                    <hr />
                    {srarchForCusID && <button className='btn btn-danger' onClick={()=>{setSearchForCusID(false)}} >Hide Search Section</button>}
                </div>
                {srarchForCusID && <div>
                                        <br />
                                        <h3>Search for Customer ID</h3>
                                        <hr />
                                        <Search table={'CUSTOMERS'}></Search>
                                    </div>}

        </div>
    )
}

export default Delete;
