import React, { useState } from 'react'

const CCustomer =()=>{

    const [custInformation, setCustInformation] = useState({
        "First Name": null,
        "Middle Name": null,
        "Last Name": null,
        "Sex": null,
        "Phone Number": null,
        "Email Address": null,
        "Date of birth": null,
        "Address": null,
    })

    const inputDetect=(value, key)=>{
        setCustInformation({...custInformation, key:value})
    }

    const fields = Object.keys(custInformation).map(
        (key)=>{
            return(
                <div>
                    <label>
                        {key}
                        <input type="text" placeholder={key} onChange={(e)=>{inputDetect(e.target.value, key)}} />
                    </label>
                </div>
                )

        }
    )

    const handleSubmit=(e)=>{
        e.preventDefault()
        console.log(custInformation);
    }

    return(
        <div>
            <h1>This is Create Customer</h1>
            <form onSubmit={handleSubmit}>
                {fields}
                <input type="submit" value="Submit" />
            </form>
        </div>
    )
}

export default CCustomer;
