import React, { useState } from 'react'
import Create from '../Create';
import axios from 'axios'



const COrder =()=>{
    const [showDisplay, setShowDisplay] = useState(false)
    const [gotData, setGotData] = useState([])
    const [fk, setfk] =useState([])

    const getData=()=>{
        var sql = "SELECT CusID FROM CUSTOMERS;"
        axios.post('/api/show_data/', {sql:sql}).then(res=>{
            setGotData(res.data)
            console.log(res.data)
        }).catch(err=>{
            console.log(err)
        })

        var temp = []

        for (var i in gotData){
            temp.push(gotData[i]['CusID'])
        }

        setShowDisplay(true)
        setfk(temp)
    }

    const CusidDD = fk.map(
            (key)=>{
                return (
                    <>
                        <option>{key}</option>
                    </>
                )
            }
    )


    return(
        <div>
        <hr />
            <button onClick={getData} className='btn btn-danger' >Create A New Order</button>
            {showDisplay &&
                <div>
                    <form className="aa" onSubmit={(e)=>{e.preventDefault()}}>
                        <div  class="input-group-prepend" >
                            <span class="input-group-text">Select Customer ID</span>
                        </div>
                            <select class="form-control">
                                <option>"--SELECT--</option>
                                {CusidDD}
                            </select> 
                            <div style={{textAlign:'left'}}>
                                <button className='btn btn-outline-danger'>Discard</button>
                                <br />
                                <button  className='btn btn-outline-success'>Register a new order</button>
                            </div>
                    </form>
                </div>

            }
        </div>
    )
}

export default COrder;
