import React, { useEffect, useState } from 'react'
import axios from 'axios' 


const Display=(props)=>{

    // const [gotData, setgotData] = useState(props.gotData)
    const [found, setfound] = useState(false)
    const [columns, setColumns] = useState([])
    useEffect(() => {
        setfound(props.gotData.length > 0)
        const cols = found ? Object.keys(props.gotData[0]) : ['No Data Found']
        setColumns(cols)
      });

    console.log(props.gotData)

    const tableCols = columns.map(
        (i)=>{
            return(
                <th>
                    {i}
                </th>
            )
        }
    )
    const rowRender = columns.map(
        (i)=>{
            return(
                <th>
                    {i}
                </th>
            )
        }
    )


   return(
       <div>
           {
               (
                   ()=>{
                       if(!found){
                           return(
                               <div>
                                   <h2>No data found</h2>
                               </div>
                           )
                       }
                       else{
                           return(
                               <div>
                                   <h2>Matching data</h2>
                                   <table className="table table-danger">
                                    <thead>
                                        <tr>
                                            {tableCols}
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {rowRender}
                                    </tbody>
                                </table>
                               </div>
                           )
                       }
                   }
               )()
           }
       </div>
   )
}

export default Display;
