import React, { useState } from 'react'
import Create from '../Create';
import axios from 'axios'



const COrder =()=>{
    const [showDisplay, setShowDisplay] = useState(false)
    const [gotData, setGotData] = useState([])
    const [fk, setfk] =useState([])

    const getData=()=>{
        var sql = "SELECT CusID FROM CUSTOMERS;"
        axios.post('/api/show_data/', {sql:sql}).then(res=>{
            setGotData(res.data)
            console.log(res.data)
        }).catch(err=>{
            console.log(err)
        })

        var temp = []

        for (var i in gotData){
            temp.push(gotData[i]['CusID'])
        }

        setShowDisplay(true)
        setfk(temp)
    }

    const CusidDD = fk.map(
            (key)=>{
                return (
                    <div>
                        <option>{key}</option>
                    </div>
                )
            }
    )


    return(
        <div>
            <button onClick={getData} className='btn btn-danger' >Create A New Order</button>
            {showDisplay &&
                <div>
                    <form className="aa" onSubmit={(e)=>{e.preventDefault()}}>
                        <label>Customer ID</label>
                       <select>{CusidDD}</select> 
                    </form>
                        <div className='inf'>
                            <button>X</button>
                        </div>
                </div>

            }
        </div>
    )
}

export default COrder;
