import React, { useState } from 'react'
import { SearchKeys, CusTypes, custColNames, custDB2N, columns } from './db'
import axios from 'axios'
import Display from './Display'

// pass tableName, searchKeys

const Search =(props)=> {
    const [rows, setRows] = useState([])
    const [showDisplay, setShowDisplay] = useState(false)
    const [gotData, setGotData] = useState([])
    const [info, setinfo] = useState({
        table: 'CUSTOMERS',
        key: 'First Name',
        value: '',
        delKeyType: 'text',
        column: 'fname',
        sql: '',
        api: "'/api/show_data/'",
        searchKeys: Object.keys(SearchKeys)
    })


    const delTypeChange =(value)=>{
        setinfo({
            ...info,  
            key:value,
            delKeyType: CusTypes[value + " type"],
            column: custColNames[value]
        });
    }
    const showMayBeItems=(e)=>{
        e.preventDefault()
        axios.post('/api/show_data/', info).then(res=>{
                setGotData(res.data)
            }).catch(err=>{
                console.log(err)
            })
        setShowDisplay(true)

    }
    const valueChange =(value)=>{
        setinfo({
            ...info, 
            value:value,
            sql: 'SELECT * FROM ' + info.table + ' WHERE ' + info.column + " = '" + value +"'" 
        });
        setShowDisplay(false)
    }

    const options = info.searchKeys.map(
        (i)=>{
            return(
                <option value={i}>{i}</option>
            )
        }
    )

    return(
        <div>
            <hr />
                <h1>Here you can delete CUSTOMERS Information</h1>
            <hr />
            <div className="form">
                <form style={{textAlign:'left', padding:'0% 70% 0% 3%'}}>
                <label>Delete Using</label>
                <select onChange={(e)=>{delTypeChange(e.target.value)}}>
                    {options}
                </select>
                <label>Delete key</label>
                <input type={info.delKeyType} onChange={(e)=>{valueChange(e.target.value)}}/>
                    <br />
                    <input className="btn btn-outline-danger" type="submit" value="Search" onClick={showMayBeItems}/>
                </form>
            </div>
            {
                (
                    ()=>{
                        if(showDisplay){
                            return(
                                <div>
                                    <h1>Matched Data with given search request</h1>
                                    <Display gotData={gotData} db2n={custDB2N}></Display>
                                </div>
                            )
                        }
                    }
                )()
            }
        </div>
    )
}

export default Search;
