import React, { useState } from 'react'
import { InputGroup }from 'react-bootstrap'

const CCustomer =()=>{

    const [custInformation, setCustInformation] = useState({
        "First Name": null,
        "Middle Name": null,
        "Last Name": null,
        "Sex": null,
        "Phone Number": null,
        "Email Address": null,
        "Date of birth": null,
        "Address": null,
    })
    const[types, setTypes] = useState({
        "First Name type": "text",
        "Middle Name type": "text",
        "Last Name type": "text",
        "Sex type": "text",
        "Phone Number type": "text",
        "Email Address type": "email",
        "Date of birth type": "date",
        "Address type": "address",
    })


    const inputDetect=(value, key)=>{
        const adder = {...custInformation}
        adder[key] = value
        setCustInformation(adder)
    }

    const fields = Object.keys(custInformation).map(
        (key)=>{
            return(
                <div className="input-group mb-3">
                    <label>
                        {key}
                        <input type={types[key + " type"]} placeholder={key} onChange={(e)=>{inputDetect(e.target.value, key)}} />
                    </label>
                </div>
                )

        }
    )

    const handleSubmit=(e)=>{
        e.preventDefault()
        console.log(custInformation);
    }

    return(
        <div>
            <h1>This is Create Customer</h1>
            <div className="form-group">
                <form onSubmit={handleSubmit}>
                    {fields}
                    <input type="submit" value="Submit" />
                </form>
            </div>
        </div>
    )
}

export default CCustomer;
