import React, { useState } from 'react'
import { DelKeys, CusTypes, custColNames } from '../db'
import axios from 'axios'


const DCustomer =()=>{

    const [rows, setRows] = useState([])
    const [delInfo, setDelInfo] = useState({
        key: 'First Name',
        value: '',
        delKeyType: 'text',
        column: 'fname',
        sql: ''
    })


    const delTypeChange =(value)=>{
        console.log(value)
        setDelInfo({
            ...delInfo, 
            key:value,
            delKeyType: CusTypes[value + " type"],
            column: custColNames[value]
        });
    }

    const showMayBeDelItems=(e)=>{
        e.preventDefault()
        axios.post('/api/show_data', delInfo).then(res=>{
            console.log(res.data)
            setRows(res.data);
            console.log('GOT')
        }).catch(err=>{
            console.log(err)
        })
        console.log('sent')
    }

    const options = Object.keys(DelKeys).map(
        (i)=>{
            return(
                <option value={i}>{i}</option>
            )
        }
    )

    console.log(delInfo)

    return(
        <div>
            <hr />
                <h1>Here you can delete CUSTOMERS Information</h1>
            <hr />
            <div className="form">
                <form style={{textAlign:'left', padding:'0% 70% 0% 3%'}}>
                <label>Delete Using</label>
                <select onChange={(e)=>{delTypeChange(e.target.value)}}>
                    {options}
                </select>
                <label>Delete key</label>
                <input type={delInfo.delKeyType} onChange={(e)=>{
                                                setDelInfo({...delInfo, value : e.target.value});
                                                setDelInfo({...delInfo, sql: 'SELECT * FROM CUSTOMERS WHERE ' + delInfo.column + " = '" + delInfo.value +"'" })

                                                }
                                    }/>
                    <br />
                    <input className="btn btn-outline-danger" type="submit" value="Search" onClick={showMayBeDelItems}/>
                </form>
            </div>

        </div>
    )
}

export default DCustomer;
