import React, { useState } from 'react'



const CustomerIndex =()=>{

    const currentDomain = '/Customer';
    const [booleans, setBooleans] = useState({
        C : false,
        R : false,
        U: false,
        D: false
    })

    const setDisplay =(code)=>{
        setBooleans({
            C: code === 'C',
            R: code === 'R',
            U: code === 'U',
            D: code === 'D'
        })
    }
    const[names, setNames] = useState([
        {
            label: "Add Customer",
            action: ()=>{setDisplay('C')}
        },{
            label: "View Registered Customer",
            action: ''
        },{
            label: "Update Customer Info",
            action: ''
        },{
            label: "Delete Customer Info",
            action: ''
        },
    ])

    const buttons = names.map(
        (i)=>{
            return (
                <button className="btn btn-danger" onClick={i.action}>
                    {i.label}
                </button>
            )
        }
    )

    const CCustomer = [0].map(
        (i)=>{
            return(
                <div>
                    <h1>This is C Customer</h1>
                </div>
            )
        }
    )

    return(
        <div style={{textAlign:'center'}} >
            <h2 >Manage Customer information</h2>
                <button className='btn btn-outline-danger' onClick={()=>{setDisplay('C')}}>
                    Add Customer
                </button>
                <button className='btn btn-outline-danger' >
                    View Registered Customer
                </button>
                <button className='btn btn-outline-danger' >
                    Update Customer Info
                </button>
                <button className='btn btn-outline-danger' >
                    Delete Customer Info
                </button>
                {buttons}
        </div>
    )
}

export default CustomerIndex;
