import React, { useState, useEffect } from 'react'
import Information from './Information';
import { animated, useSpring} from 'react-spring';
import {sqls, cols} from './db'
import axios from 'axios'
import CustomDisplay from './Display/CustomDisplay';


const Welcome =()=>{

    const [showCustomDisplay, setShowCustomDisplay] = useState(false)
    const [gotData, setGotData] = useState([])
    const [temp, settemp] = useState([])
    const [show, setShow] = useState(false)
    const [showSearchSegment, setShowSearchSegment] = useState(false)



    const styleTopic = useSpring({
        loop: { reverse: true },
        from: { x:-200, opacity: 0.5 },
        to: { x:200, opacity: 1 },
        config: {duration: 5000},
    })

    useEffect(() => {
        for(var i=0; i< sqls.length; i++){
            showAll(sqls[i], i);
        }
    }, [])

    const showAll =(sql, index)=>{
        axios.post('/api/show_data/', {sql:sql}).then(res=>{
            setGotData(res.data)
            console.log(res.data)
        }).catch(err=>{
            console.log(err)
        })
    setShowCustomDisplay(true)
    setShowSearchSegment(false)
    console.log(gotData)
    }


    return(
        <div style={{paddingBottom:'500px'}}>
            <animated.div style={{
                ...styleTopic
                }}>
                <hr />
                <div style={{textAlign:'center'}}>
                <h1 style={{fontStyle:'italic', fontFamily:'fantasy',fontWeight:'bold'}} >Welcome to Restaurant Management System</h1>
                <img height='200' src="https://tu.edusanjal.com/static/img/tu.png" alt="" />
                <hr />
                <h4>Tribhuwan University</h4>
                <h5>IOE, Pulchowk Campus</h5>
            </div>
            </animated.div>
            <hr />
            <div style={{textAlign:'center'}}>
                <Information normal={''} ></Information>
            </div>
            <hr />
            <div className='aa'>
                <h3>Latest 5 Orders</h3>
                <CustomDisplay
                    gotData={gotData} self={''} 
                    columns={[
                        'OrderID', 
                        'CustomerFirstName', 
                        'CustomerLastName', 
                        'CusID', 
                        'WaiterName', 
                        'TableNumber', 
                        'completed'
                        ]}></CustomDisplay>
            </div>
        </div>
    )
}

export default Welcome;
