import React, { useEffect, useState } from 'react'
import { useTransition, animated } from 'react-spring'


const Display=(props)=>{


    const [items, setItems] = useState('1234')

    const transitions = useTransition(items, {
      from: { opacity: 0 },
      enter: { opacity: 1 },
      leave: { opacity: 0 },
      delay: 200,
      onRest: () => setItems([]),
    })

    useEffect(() => {
      if (items.length === 0) {
        setTimeout(() => {
          setItems(5)
        }, 2000)
      }
    }, [items])

    const tableCols = (props.gotData.length > 0 ? Object.keys(props.gotData[0]) : []).map(
        (i)=>{
            return(
                <th>{props.db2n[i]}</th>
            )
        }
    )

    const rowRender2 =(n)=> (props.gotData.length > 0 ? Object.keys(props.gotData[0]) : []).map(
        (keys)=>{
            if (keys === 'dob' && props.gotData[n][keys] !== null ){
                return(
                    <td>
                        {props.gotData[n][keys].substring(0,9)}
                    </td>
                )
            }
            else{
              return (
            <td>
                {props.gotData[n][keys]}
            </td>
            )  
            }

        }
    )

    const rowRender = (props.gotData.length > 0 ? [...props.gotData.keys()] : []).map(
        (index)=>{
            return(
                <tr>
                    {rowRender2(index)}
                </tr>
            )
        }
    )


   return(
       <div style={{padding:'10%% 10% 10% 10%'}}>
           {
               (
                   ()=>{
                       if(!(props.gotData.length > 0)){
                           return(
                               <div>
                                   <h2>No data found</h2>
                               </div>
                           )
                       }
                       else{
                           return(
                               <div>

                                   <table className="table table-danger">
                                    <thead>
                                        <tr>
                                            {tableCols}
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {rowRender}
                                    </tbody>
                                </table>
                               </div>
                           )
                       }
                   }
               )()
           }
       </div>
   )
}

export default Display;
