import React, { useState } from 'react'
import Search from '../Search'
import Delete from '../Delete'
import { StickyContainer, Sticky } from 'react-sticky';

const DCustomer =()=>{

    const [srarchForCusID, setSrarchForCusID] = useState(false)

    const deleteClicked =()=>{

    }

    return(
        <div>
            <hr />
            {/* <div>
                <Delete></Delete>
            </div> */}
            <StickyContainer>
        {/* Other elements can be in between `StickyContainer` and `Sticky`,
        but certain styles can break the positioning logic used. */}
        <Sticky>
          {({
            style,

            // the following are also available but unused in this example
            isSticky,
            wasSticky,
            distanceFromTop,
            distanceFromBottom,
            calculatedHeight
          }) => (
            <header style={style}>
              {/* ... */}
            </header>
          )}
        </Sticky>
        {/* ... */}
      </StickyContainer>
            <StickyContainer>
            {
                <Sticky>
                    {{}}
                </Sticky>
            }
                <div className='inf1' style={{textAlign:'left', padding:'1% 30% 1% 1%'}}>
                    <label className="form-check-label">Enter Customer ID to delete</label>
                    <input className="form-control" type="number"/>
                    <button className='btn btn-outline-danger' onClick={deleteClicked}>DELETE</button>
                    <hr />
                    <button className='btn btn-primary' onClick={()=>{setSrarchForCusID(true)}} >Search for Customer ID</button>
                    <hr />
                    {(()=>{
                        if(srarchForCusID){
                            return(
                                <button className='btn btn-danger' onClick={()=>{setSrarchForCusID(false)}} >Hide Search Section</button>
                            )
                        }
                    })()}
                </div>
            </StickyContainer>
            {
                (
                    ()=>{
                        if(srarchForCusID){
                            return(
                                <div>
                                <br />
                                    <h3>Search for Customer ID</h3>
                                    <hr />
                                    <Search table={'CUSTOMERS'}></Search>
                                </div>
                            )
                        }
                    }
                )()
            }


        </div>
    )
}

export default DCustomer;
