import React from 'react'
import { animated, useSpring, Spring } from 'react-spring';


const Information =()=>{

    const members = [
        {
            name: 'Abhay Nepal',
            roll: '075 BEI 003'
        },{
            name: 'Gokarna Adhikari',
            roll: '075 BEI 014'
        },{
            name: 'Kshitiz Dhakal',
            roll: '075 BEI 015'
        },{
            name: 'Love Panta',
            roll: '075 BEI 016'
        }
    ]
    const names = members.map(
        (i)=>{
            return(
                <tr>
                    <td>{i.name}</td>
                    <td>{i.roll}</td>
                </tr>
            )
        }
    )


    return(
        <div>
            <Spring
            loop
            from={{ opacity: 0.8, color: 'red' }}
            to={[
            { opacity: 0.6, color: '#ffffff' },
            { opacity: 1, color: '#000000' },
            ]}

            >
                {
                    (styles)=>(
                        <animated.div style={styles}>
                            <h1>DBMS Project</h1>
                            <hr />
                            <h3 style={{textAlign:'left'}}>Completed By</h3>
                            <hr />
                        </animated.div>
                    )
                }
            </Spring>
            <table className='table table-dark'>
                            <thead>
                                <tr>
                                    <th>Name</th>
                                    <th>Roll Number</th>
                                </tr>
                            </thead>
                            <tbody>
                                {names}
                            </tbody>
                        </table>
        </div>
    )
}

export default Information;
