import React, { useState } from 'react'
import { Link } from "react-router-dom";
import CCustomer from './CCustomer'
import DCustomer from './DCustomer'
import RCustomer from './RCustomer'
import UCustomer from './RCustomer'

import { Navbar, Button, Table } from 'react-bootstrap';



const CustomerIndex =()=>{

    const currentDomain = '/Customer';
    const [linking, setlinking] = useState([    // [path, component]
        ['/Create', "Add Customer"],
        ['/Read', "View Registered Customer"],
        ['/Update', "Update Customer Info"],
        ['/Delete', "Delete Customer"]

    ])

    const linkInfo = linking.map(
    (i)=>{
        return (
                <div to={currentDomain + i[0]} style={{textDecoration:'none'}} className='nav-link'>
                        <span className='btn btn-outline-danger'>{i[1]}</span>
                </div>
                )
    }
    )

    return(
        <div>
            <h2 style={{textAlign:'center'}} >Manage Customer information</h2>

            <Table variant="dark">
                {linkInfo}
            </Table>
        </div>
    )
}

export default CustomerIndex;
