import axios from 'axios'
import Search from '../Search/Search'
import SelectOptions from '../SelectOptions'
import Information from '../Information'


const COrder =()=>{

    const [info, setInfo] = useState({})
    const [formShow, setFormShow] = useState(true)

    const submitFunction=(info)=>{
        console.log(info)
        setFormShow(false)
    }

    return(
        <>
            { formShow &&
                <div>
                    <SelectOptions submitFunction={submitFunction} mainTable={'ORDER_'} selectTable={['CUSTOMERS', 'WAITER']} ></SelectOptions>
                </div>
            }
            <div>
            {!formShow &&
                <button className='btn btn-dark' onClick={()=>{setFormShow(true)}}>
                    Register Another Order
                </button>
            }
            </div>
        </>
    )
}

export default COrder;
