import React, { useState } from 'react'
import { DelKeys } from '../db'

const DCustomer =()=>{

    const [delType, setDelType] = useState('First Name')
    const [delValue, setDelValue] = useState('')


    const delCustomer=(e)=>{
        e.preventDefault()

    }

    const options = Object.keys(DelKeys).map(
        (i)=>{
            return(
                <option value="">{i}</option>
            )
        }
    )


    return(
        <div>
            <hr />
                <h1>Here you can delete CUSTOMERS Information</h1>
            <hr />
            <div className="form">
                <form style={{textAlign:'left', padding:'0% 70% 0% 3%'}}>
                <label>Select the way you want to delete the Customer Info with</label>
                <select onChange={(e)=>{setDelType(e.target.value)}}>
                    {options}
                </select>
                <label>Delete key</label>
                <input type="text" name="" id="" />
                    <br />
                    <input className="btn btn-outline-danger" type="submit" value="ADD" onClick={delCustomer}/>
                </form>
            </div>

        </div>
    )
}

export default DCustomer;
