import React, { useState } from 'react'



const CustomerIndex =()=>{

    const currentDomain = '/Customer';
    const [booleans, setBooleans] = useState({
        C : false,
        R : false,
        U: false,
        D: false
    })

    const setDisplay =(code)=>{
        setBooleans({
            C: code === 'C',
            R: code === 'R',
            U: code === 'U',
            D: code === 'D'
        })
    }
    const[names, setNames] = useState([
        {
            label: "Add Customer",
            action: ()=>{setDisplay('C')}
        },{
            label: "View Registered Customer",
            action: ()=>{setDisplay('R')}
        },{
            label: "Update Customer Info",
            action: ()=>{setDisplay('U')}
        },{
            label: "Delete Customer Info",
            action: ()=>{setDisplay('D')}
        },
    ])

    const buttons = names.map(
        (i)=>{
            return (
                <>
                    <button className="btn btn-outline-danger" onClick={i.action}>
                        {i.label}
                    </button> 
                </>
            )
        }
    )

    const CCustomer = [0].map(
        (i)=>{
            return(
                <div>
                    <h1>This is C Customer</h1>
                </div>
            )
        }
    )

    return(
        <div style={{textAlign:'center'}} >
            <h2 >Manage Customer information</h2>
                {buttons}
        </div>
    )
}

export default CustomerIndex;
