import React, { useState } from 'react'
import { Link } from "react-router-dom";
import { Nav } from 'react-bootstrap';



const CustomerIndex =()=>{

    const currentDomain = '/Customer';
    const [linking, setlinking] = useState([    // [path, component]
        ['/Create', "Add Customer"],
        ['/Read', "View Registered Customer"],
        ['/Update', "Update Customer Info"],
        ['/Delete', "Delete Customer"]

    ])

    const linkInfo = linking.map(
    (i)=>{
        return (
            <div>
                <Link to={currentDomain + i[0]} style={{textDecoration:'none'}}>
                        {i[1]}
                </Link>
            </div>
                )
    }
    )




    return(
        <div>
            <h1>This is Customer</h1>
            {linkInfo}
        </div>
    )
}

export default CustomerIndex;
