import React, { useState } from 'react'
import { SearchKeys, dataType, columns, dontIncludeInFrom } from '../db'


const Update = (props) =>{

    const [sql, setSQL] = useState('')
    const [id, setID] = useState('')
    const [nextStep, setNextStep] = useState(false)
    const [changedValues, setChangedValues] = useState({})
    const [confirmIDUpdate, setcidu] = useState(true)

    const idUpdate =(e)=>{
        e.preventDefault()
        if (id !== ''){ 
            setNextStep(true)
        }
    }

    const changed =(value, key)=>{
        var temp = {}
        temp[key] = value
        setChangedValues(
            {
                ...changedValues,
                ...temp
            }
        )
    }

    const applyChanges=(e)=>{
        e.preventDefault()
        console.log(changedValues)
    }

    const renderChangedValues=Object.keys(changedValues).map(
        (key)=>{
            return(
                <div>
                    "{key}"" will be changed to "{changedValues[key]}""
                </div>
            )
        }
    )

    const fields = Object.keys(SearchKeys[props.table]).map(
        (key)=>{
            return(
                <>
                   {!dontIncludeInFrom[props.table].includes(key) && <div>
                        <label>{key}</label>
                        { key !== 'Sex' && <input type={dataType[props.table][key + " type"]} onChange={(e)=>{changed(e.target.value, key)}} />}
                        { key === 'Sex' && <input type='text' maxLength="1" placeholder='M or F or O' onChange={(e)=>{changed(e.target.value, key)}}/>}
                    </div>}
                </>
            )
        }
    )


    return(
        <div>
            <div className='updateWithID'>
                <form onSubmit={idUpdate} >
                    <label>
                        Enter the ID of the data you want to change
                    </label>
                    <input type="number" name="" id="" onChange={(e)=>{setID(e.target.value)}} />
                    <input type="submit" value="DONE"/>
                </form>
                {nextStep && <form onSubmit={(e)=>{e.preventDefault(); setcidu(true)}}>
                    <hr />
                    <label>Enter the Changes you want to apply</label>
                    {fields}
                    <input type="submit" value='APPLY' />
                </form>}
                {
                    confirmIDUpdate && <div style={{borderStyle:'dashed'}}>
                       <h5>The row in {props.table} with ID = {id} will encounter following changes:</h5> 
                        <p>{renderChangedValues}</p>
                        <button className='btn btn-danger'>CONTINUE</button>
                        <button className='btn btn-primary'>DISCARD</button>
                    </div>
                }
            </div>



        </div>
    )
}

export default Update;
