import React, { useState } from 'react'
import '../../css/Modal.css'
import axios from 'axios'


const Modal = (props)=>{

    const [gotData, setgotData] = useState([])
    const [showDisplay, setShowDisplay] = useState(false)

    const showMayBeItems=(e)=>{
        e.preventDefault()
        var sql = "DELETE FROM " + props.table + " WHERE " + props.primaryKeyName + " = '" + props.delID + "'"
        axios.post('/api/show_data/', sql).then(res=>{
                setgotData(res.data)
            }).catch(err=>{
                console.log(err)
            })
        setShowDisplay(true)

    }

    return( 
        <div className='modalBackground'>
            <div className="modalContainer">
                <div className='title'>
                    <h1>Are you sure to delete?</h1>
                    <hr />
                </div>
                <div className="bodyz">
                    <h3>Detail of the data: </h3>
                    <div>
                        <h4>ID: {props.delID}</h4>
                    </div>
                    </div>
            {!props.complexDel && <div className='footer'>
                    <button onClick={()=>{props.closeModal(false)}}>Cancel</button>
                    <button onClick={()=>props.delWithID()} >Continue</button>
                </div>}
            {props.complexDel && <div className='footer'>
                <button onClick={()=>{props.closeModal(false)}}>Cancel</button>
                <button onClick={props.complexDelz} >Continue</button>
            </div>}
            </div>

        </div>
    )
}

export default Modal;
