import axios from 'axios' 


const Display=(props)=>{

    // const [gotData, setgotData] = useState(props.gotData)
    useEffect(() => {
        setfound(props.gotData.length > 0)
      });

    console.log(props.gotData)
    console.log(props.gotData.length > 0)

    const tableCols = (props.gotData.length > 0 ? Object.keys(props.gotData[0]) : []).map(
        (i)=>{
            return(
                <th>
                    {i}
                </th>
            )
        }
    )


    const rowRender = (props.gotData.length > 0 ? props.gotData[0] : []).map(
        (i)=>{
            return(
                <th>
                    {i}
                </th>
            )
        }
    )


   return(
       <div>
           {
               (
                   ()=>{
                       if(!(props.gotData.length > 0)){
                           return(
                               <div>
                                   <h2>No data found</h2>
                               </div>
                           )
                       }
                       else{
                           return(
                               <div>
                                   <h2>Matching data</h2>
                                   <table className="table table-danger">
                                    <thead>
                                        <tr>
                                            {tableCols}
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {rowRender}
                                    </tbody>
                                </table>
                               </div>
                           )
                       }
                   }
               )()
           }
       </div>
   )
}

export default Display;
