import React, { useState } from 'react'
import { SearchKeys, dataType } from '../db'


const Update = (props) =>{

    const [sql, setSQL] = useState('')
    const [id, setID] = useState('')
    const [nextStep, setNextStep] = useState(false)

    const idUpdate =(e)=>{
        e.preventDefault()
        if (id !== ''){ 
            setNextStep(true)
        }
    }

    const applyChanges=(e)=>{
        e.preventDefault()
    }

    const fields = Object.keys(SearchKeys[props.table]).map(
        (key)=>{
            return(
                <div>
                    <label>{key}</label>
                    { key !== 'Sex' && <input type={dataType[props.table][key + " type"]} />}
                    { key == 'Sex' && <input type='text' maxLength="1" />}
                </div>
            )
        }
    )


    return(
        <div>
            <div className='updateWithID'>
                <form onSubmit={idUpdate} >
                    <label>
                        Enter the ID of the data you want to change
                    </label>
                    <input type="number" name="" id="" onChange={(e)=>{setID(e.target.value)}} />
                    <input type="submit" value="DONE"/>
                </form>
                <form onSubmit={applyChanges}>
                    <hr />
                    <label>Enter the Changes you want to apply</label>

                </form>
            </div>

            {fields}

        </div>
    )
}

export default Update;
