import React, { useState } from 'react'
import axios from 'axios'
import Search from './Search/Search'
import { primaryKey } from './db'


// prpos: mainTable, selectTable = [array]

const SelectOptions=(props)=>{
    const [showDisplay, setShowDisplay] = useState(false)
    const [gotData, setGotData] = useState({})
    // const [gotDatat, setGotDatat] = useState([])
    const [fk, setfk] =useState([])
    const [FK, setFK] = useState([])
    const [showSearchOption, setShowSearchOption] = useState(false)
    const [te, setTe] = useState('')

    const getData=()=>{
        for(var i in props.selectTable){
            var sql = "SELECT " + primaryKey[props.selectTable[i]] +  " FROM " + props.selectTable[i] + ";"
            axios.post('/api/show_data/', {sql:sql}).then((res)=>{
                var temp = JSON.parse(res.config.data).sql
                temp = temp.slice(7, -1).split(" ")
                var temp2 = gotData;
                temp2[temp[2]] = res.data
                setGotData(temp2)
            }).catch(err=>{
                console.log(err)
            })
        }
        console.log("GOTDATA")
        console.log(gotData)
        for(var q=0; q<10000000; q++);
        setShowDisplay(true)
    }


    const CusidDD = Object.keys(gotData).map(
                (key1)=>{
                    return (
                        <>
                            <option>HERE</option>
                            {
                               Object.keys(gotData[key1]).map(
                                   (key2)=>{
                                       return(
                                           <option>
                                               {key2}
                                           </option>
                                       )
                                   }
                               )
                            }
                        </>
                    )
                }
            )

    const selects = Object.keys(props.selectTable).map(
        (key)=>{
            return(
                <>
                    <span class="input-group-text">Select {props.selectTable[key]} ID</span> 
                    <select class="form-control" onChange={(e)=>{
                        var temp = FK
                        if(e.target.value === "--SELECT--"){
                            temp[key] = ''
                        }else{
                            temp[key] = e.target.value
                            }
                        setFK(temp)
                        }}>
                        <option>--SELECT--</option> 
                        {CusidDD}
                    </select> 
                </>
            )
        }
    )


    return(
        <div>
            <hr />
                <button onClick={getData} className='btn btn-danger' >Create A New {props.mainTable}</button>
            <hr />
                {showDisplay &&
                <>
                    <div className="aa">
                        <form onSubmit={(e)=>{e.preventDefault()}}>
                            <div  class="input-group-prepend" >
                                {selects}
                            </div>

                        </form>

                    </div>
                    <div className='inf'>
                        <button className='btn btn-primary' onClick={()=>{setShowSearchOption(true)}}>Search For {props.selectTable} ID</button>
                            <br /> <hr />
                            {showSearchOption && 
                            <>
                                <Search table={props.selectTable} ></Search>
                            </>

                            }
                    </div>
                </>
                }
        </div>
    )
}

export default SelectOptions;
