import React, { useState } from 'react'
import Search from '../Search'
import { Button, Navbar, NavDropdown } from 'react-bootstrap';

import Delete from '../Delete'

const DCustomer =()=>{

    const [srarchForCusID, setSrarchForCusID] = useState(false)
    const [margintop, setMargintop] = useState('0%')


    const deleteClicked =()=>{

    }

    return(
        <div>
            <hr />
            {/* <div>
                <Delete></Delete>
            </div> */}

            <Navbar sticky="top">
                <div className='inf1' style={{textAlign:'left', padding:'1% 30% 1% 1%', marginTop:margintop}}>
                    <label className="form-check-label">Enter Customer ID to delete</label>
                    <input className="form-control" type="number"/>
                    <button className='btn btn-outline-danger' onClick={deleteClicked}>DELETE</button>
                    <hr />
                    <button className='btn btn-primary' onClick={()=>{setSrarchForCusID(true);setMargintop('5%');}} >Search for Customer ID</button>
                    <hr />
                    {(()=>{
                        if(srarchForCusID){
                            return(
                                <button className='btn btn-danger' onClick={()=>{setSrarchForCusID(false);setMargintop('0%');}} >Hide Search Section</button>
                            )
                        }
                    })()}
                </div>
            </Navbar>
            {
                (
                    ()=>{
                        if(srarchForCusID){
                            return(
                                <div>
                                <br />
                                    <h3>Search for Customer ID</h3>
                                    <hr />
                                    <Search table={'CUSTOMERS'}></Search>
                                </div>
                            )
                        }
                    }
                )()
            }


        </div>
    )
}

export default DCustomer;
