import React, { useState } from 'react'
import { Link } from "react-router-dom";
import { Navbar, Button, Table } from 'react-bootstrap';



const CustomerIndex =()=>{

    const currentDomain = '/Customer';
    const [linking, setlinking] = useState([    // [path, component]
        ['/Create', "Add Customer"],
        ['/Read', "View Registered Customer"],
        ['/Update', "Update Customer Info"],
        ['/Delete', "Delete Customer"]

    ])

    const linkInfo = linking.map(
    (i)=>{
        return (
                <Link to={currentDomain + i[0]} style={{textDecoration:'none'}} className='nav-link'>
                        <Button variant='outline-light'>{i[1]}</Button>
                </Link>
                )
    }
    )

    return(
        <Table expand="lg" bg="dark" className="navbar navbar-expand bg-">
            <h2>This is Customer</h2>
            {linkInfo}
        </Table>
    )
}

export default CustomerIndex;
