import React, { useState } from 'react'
import { SearchKeys, columns, dataType, keyValue } from './db'
import axios from 'axios'
import Display from './Display'
import Decorate from './Decorate'

/*
props needed:
    table
*/

const ComplexSearch =(props)=> {
    const[numberOfSearchParameters, setnosp] = useState(1)
    const[selectedSearchParameters, setssp] = useState([])
    const [showDisplay, setShowDisplay] = useState(false)
    const [gotData, setGotData] = useState([])
    const [info, setinfo] = useState({
        table: props.table,
        key: Object.keys(SearchKeys[props.table])[0],
        value: [],
        keyType: 'text',
        column:[],
        keyValue: keyValue[props.table],
        api: "'/api/show_data/'",

        searchKeys: Object.keys(SearchKeys[props.table])
    })

    const paraNumChange =(value)=>{
        setnosp(value)
    }

    const typeChange =(value)=>{
        setShowDisplay(false)
        var c = [...info.column, SearchKeys[info.table][value]], v = [...info.key, v]
        setinfo({
            ...info,  
            key:v,
            keyType: dataType[info.table][value + " type"],
            column: c,
        });
    }
    const showMayBeItems=(e)=>{
        e.preventDefault()
        axios.post('/api/show_data/', info).then(res=>{
                setGotData(res.data)
            }).catch(err=>{
                console.log(err)
            })
        setShowDisplay(true)

    }
    const valueChange =(value)=>{
        setShowDisplay(false)
        var temp = info.keyValue


    }

    const options = info.searchKeys.map(
        (i)=>{
            return(
                <>
                {
                    (
                        ()=>{
                            if(info.keyValue[i] === null){
                                return(
                                    <option value={i}>{i}</option>
                                )
                            }
                        }
                    )()
                }
                </>
            )
        }
    )

    const selectOptions = [...Array(Object.keys(SearchKeys[props.table]).length).keys()].map(
        (i)=>{
            return(
                <option>
                    {i+1}
                </option>
            )
        }
    )

    const keyOptions = [...Array(numberOfSearchParameters).keys()].map(
        (i)=>{
            return(
                <div className="form">
                    <form style={{textAlign:'left', padding:'0% 70% 0% 3%'}}>
                        <label className="form-check-label">Search Key {i+1}</label>
                            <select className="form-control" onChange={(e)=>{typeChange(e.target.value)}}>
                                {options}
                            </select>
                        <label className="form-check-label">Search value</label>
                            <input className="form-control" type={info.keyType} onChange={(e)=>{valueChange(e.target.value)}}/>
                        <br />
                        <input className="btn btn-outline-danger" type="submit" value="Search" onClick={showMayBeItems}/>
                    </form>
                </div>
            )
        }
    )

    return(
        <div>
        <h4>Advanced Search</h4>
        <hr />
        <div className="form">
        <form style={{padding:'0% 20% 0% 30%'}}>
            <label className="form-check-label">Enter the number of parameters you want to search with</label>
            <select className="form-control" onChange={(e)=>{paraNumChange(parseInt(e.target.value))}}>
                {selectOptions}
            </select>
        </form>
        <hr />

        {keyOptions}

        </div>
            {
                (
                    ()=>{
                        if(showDisplay){
                            return(
                                <div>
                                    <hr />
                                    <Decorate style={{textAlign:'right'}} displayText={'>  SEARCH RESULT FROM PROVIDED REQUEST  <'} ></Decorate>
                                    <hr />
                                    <Display gotData={gotData} db2n={columns[info.table]}></Display>
                                </div>
                            )
                        }
                    }
                )()
            }
        </div>
    )
}

export default ComplexSearch;
