import React, { useState } from 'react'
import { InputGroup }from 'react-bootstrap'

const CCustomer =()=>{

    const [custInformation, setCustInformation] = useState({
        "First Name": null,
        "Middle Name": null,
        "Last Name": null,
        "Sex": null,
        "Phone Number": null,
        "Email Address": null,
        "Date of birth": null,
        "Address": null,
    })
    const[types, setTypes] = useState({
        "First Name class": "form-control",
        "Middle Name class": "",
        "Last Name class": "",
        "Sex class": "",
        "Phone Number class": "",
        "Email Address class": "",
        "Date of birth class": "",
        "Address class": "",

        "First Name id": "",
        "Middle Name id": "",
        "Last Name id": "",
        "Sex id": "",
        "Phone Number id": "",
        "Email Address id": "",
        "Date of birth id": "",
        "Address id": "",

        "First Name type": "text",
        "Middle Name type": "text",
        "Last Name type": "text",
        "Sex type": "checkbox",
        "Phone Number type": "text",
        "Email Address type": "email",
        "Date of birth type": "date",
        "Address type": "address",
    })


    const inputDetect=(value, key)=>{
        const adder = {...custInformation}
        adder[key] = value
        setCustInformation(adder)
    }

    const fields = Object.keys(custInformation).map(
        (key)=>{
            return(
                <div className="i">
                    <label>
                        {key}
                        <input  className={types[key + " class"]}
                                id={types[key + " id"]}
                                type={types[key + " type"]} 
                                placeholder={key} 
                                onChange={(e)=>{inputDetect(e.target.value, key)}} 

                                />
                    </label>
                </div>
                )

        }
    )

    const handleSubmit=(e)=>{
        e.preventDefault()
        console.log(custInformation);
    }

    return(
        <div>
        <hr />
            <h1>This is Create Customer</h1>
            <div className="form-group">
                <form onSubmit={handleSubmit} style={{textAlign:'left', paddingLeft:'3%'}}>
                    {fields}
                    <input type="submit" value="Submit" />
                </form>
            </div>
        </div>
    )
}

export default CCustomer;
